/// The output of a function tool call.
nonisolated public struct OpenAIFunctionToolCallOutput: Encodable, Sendable {
    /// The unique ID of the function tool call generated by the model.
    public let callID: String
    
    /// Text, image, or file output of the function tool call.
    public let output: Output
    
    /// The type of the function tool call output. Always `function_call_output`.
    public let type = "function_call_output"
    
    /// The unique ID of the function tool call output. Populated when this item is returned via API.
    public let id: String?
    
    /// The status of the item.
    ///
    /// One of `in_progress`, `completed`, or `incomplete`. Populated when items are returned via API.
    public let status: Status?
    
    /// Creates a new function call output item parameter.
    /// - Parameters:
    ///   - callID: The unique ID of the function tool call generated by the model.
    ///   - output: Text, image, or file output of the function tool call.
    ///   - id: The unique ID of the function tool call output.
    ///   - status: The status of the item.
    public init(
        callID: String,
        output: Output,
        id: String? = nil,
        status: FunctionCallItemStatus? = nil
    ) {
        self.callID = callID
        self.output = output
        self.id = id
        self.status = status
    }
    
    private enum CodingKeys: String, CodingKey {
        case callID = "call_id"
        case output
        case type
        case id
        case status
    }
    
    public func encode(to encoder: Encoder) throws {
        var container = encoder.container(keyedBy: CodingKeys.self)
        try container.encode(callID, forKey: .callID)
        try container.encode(output, forKey: .output)
        try container.encode(type, forKey: .type)
        try container.encodeIfPresent(id, forKey: .id)
        try container.encodeIfPresent(status, forKey: .status)
    }
}

extension OpenAIFunctionToolCallOutput {
    /// The output of the function tool call.
    public enum Output: Encodable, Sendable {
        /// A JSON string of the output of the function tool call.
        case text(String)

        /// An array of text, image, or file content items.
        case items([OpenAIInputContent])

        public func encode(to encoder: Encoder) throws {
            var container = encoder.singleValueContainer()
            switch self {
            case .text(let value):
                try container.encode(value)
            case .items(let items):
                try container.encode(items)
            }
        }
    }
}

extension FunctionToolCallOutput {
    /// The status of the function tool call.
    public enum Status: String, Encodable, Sendable {
        case inProgress = "in_progress"
        case completed
        case incomplete
    }
}
